def split_behaviour(row):
    if row['behaviour_title'] == 'Button A':
        row['title'] = 'button_a_' + row['xy']
        if row['xy'] == 'x':
            row['value'] = re.findall(r"X\+(\d*),\sY\+(\d*)", row['behaviour_value'])[0][0]
        elif row['xy'] == 'y':
            row['value'] = re.findall(r"X\+(\d*),\sY\+(\d*)", row['behaviour_value'])[0][1]

    elif row['behaviour_title'] == 'Button B':
        row['title'] = 'button_b_' + row['xy']
        if row['xy'] == 'x':
            row['value'] = re.findall(r"X\+(\d*),\sY\+(\d*)", row['behaviour_value'])[0][0]
        elif row['xy'] == 'y':
            row['value'] = re.findall(r"X\+(\d*),\sY\+(\d*)", row['behaviour_value'])[0][1]
    else:
        row['title'] = 'prize_' + row['xy']
        if row['xy'] == 'x':
            row['value'] = re.findall(r"X\=(\d*),\sY\=(\d*)", row['behaviour_value'])[0][0]
        elif row['xy'] == 'y':
            row['value'] = re.findall(r"X\=(\d*),\sY\=(\d*)", row['behaviour_value'])[0][1]

    return row
