def get_operand(operand, is_combo, ra, rb, rc):

    o = -1
    if is_combo==False:
        o = operand
    elif operand in range(4):
        o = operand
    elif operand == 4:
        o = ra
    elif operand == 5:
        o = rb
    elif operand == 6:
        o = rc
    elif operand == 7:
